import React, { Component } from "react";


class Doctorform extends Component {
    constructor() {
        super();
        this.state = {
            firstname: '',
            lastname: '',
            mobilenumber: '',
            Email: '',
            speciality: '',
            formErrors: {}
        }
        this.initialState = this.state;
    }
    handleFormValidation() {
        const { firstname, lastname, mobilenumber, Email, speciality } = this.state;
        let formErrors = {};
        let formIsValid = true;

        if (!firstname) {
            formIsValid = false;
            formErrors["firstnameErr"] = "Name is required.";
        }
        console.log(firstname);


        if (!lastname) {
            formIsValid = false;
            formErrors["lastnameErr"] = "Name is required.";
        }
        console.log(lastname);
        if (!mobilenumber.length) {
            formIsValid = false;
            formErrors["mobilenumberErr"] = "mobile number is required.";
        }
        else {
            var mobPattern = /^(?:(?:\\+|0{0,2})91(\s*[\\-]\s*)?|[0]?)?[789]\d{9}$/;
            if (!mobPattern.test(mobilenumber)) {
                formIsValid = false;
                formErrors["mobilenumberErr"] = "Invalid mobilenumber ";
            }
        }

        if (!Email) {
            formIsValid = false;
            formErrors["EmailErr"] = "Email id is required.";
        }
        else if (!(/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(Email))) {

            formIsValid = false;
            formErrors["EmailErr"] = "Invalid Email ";
        }
        if (speciality === '' || speciality === "select") {
            formIsValid = false;
            formErrors["specialityErr"] = "Select speciality";
        }

        this.setState({ formErrors: formErrors });
        return formIsValid;
    }

    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
        console.log(value)

    }


    handleSubmit = (e) => {
        e.preventDefault();
        if (this.handleFormValidation()) {
            alert('You have been successfully registered.')
            this.setState(this.initialState)
        }
        console.log(this.initialState)
    }
    render() {
        const { firstnameErr, lastnameErr, EmailErr, dobErr, genderErr, mobilenumberErr, specialityErr } = this.state.formErrors;


        return (

            <div className="container ">

                <div className="border border-primary doc-form m-5 p-4">
                    <h3 className="m-2">Registration From</h3>
                    <form onSubmit={this.handleSubmit}>

                        <div class="form-row  ">
                            <div className=" form-group col-md-4">
                                <label >FirstName*

                                </label>
                                <input type="text"
                                    name="firstname"
                                    value={this.state.firstname}
                                    onChange={this.handleChange}

                                    className="form-control" placeholder="firstName"
                                    error={firstnameErr ? ' firstnameErr' : ''}

                                />
                                {firstnameErr &&
                                    <div style={{ color: "red", paddingBottom: 10 }}>{firstnameErr}</div>
                                }
                            </div>
                            <div className="form-group col-md-4">
                                <label >LastName*</label>
                                <input type="text" name="lastname" value={this.state.lastname} onChange={this.handleChange} className="form-control" placeholder="LastName" error={lastnameErr ? ' firstnameErr' : ''} />
                                {lastnameErr && <div style={{ color: "red", paddingBottom: 10 }}>{lastnameErr}</div>}
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-4">
                                <label >Mobile Number*</label>
                                <input type='text' name="mobilenumber" maxlength="10" id="phone"
                                    value={this.state.mobilenumber}
                                    onChange={this.handleChange}
                                    className="form-control" placeholder="Mobile"
                                    error={mobilenumberErr ? ' showError' : ''} />
                                {mobilenumberErr && <div style={{ color: "red", paddingBottom: 10 }}>{mobilenumberErr}</div>}
                            </div>
                            <div className="form-group col-md-4">
                                <label >Email</label>
                                <input type="text" name="Email"
                                    className="form-control" placeholder="Email"
                                    value={this.state.Email}
                                    onChange={this.handleChange}
                                    error={EmailErr ? ' showError' : ''} />
                                {EmailErr &&
                                    <div style={{ color: "red", paddingBottom: 10 }}>{EmailErr}</div>
                                }

                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group col-md-4">
                                <label>Specialization</label>
                                <select className="form-control" name="city"
                                    value={this.state.city}
                                    onChange={this.handleChange}
                                    className={cityErr ? ' showError' : ''}     >
                                    <option selected>Choose...</option>
                                    <option>Dental</option>
                                    <option>Cardio</option>
                                    <option>Diagnostic</option>
                                    <option>ENT</option>
                                </select>
                                {cityErr &&
                                    <div style={{ color: "red", paddingBottom: 10 }}>{cityErr}</div>
                                }
                            </div>
                            <div className="form-group col-md-4">
                                <label for="inputState">Available</label>
                                <select id="inputState" className="form-control">
                                    <option selected>Choose...</option>
                                    <option>True</option>
                                    <option>False</option>
                                </select>
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-4">
                                <label for="inputState">Ward</label>
                                <select id="inputState" className="form-control">
                                    <option selected>Choose...</option>
                                    <option>General </option>
                                    <option>ICU</option>
                                    <option>NID</option>
                                </select>
                            </div>

                            <div className="form-group my-2 col-md-6">

                                <button type="submit" className=" mx-0 px-5 btn btn-primary">Sign in</button>

                                <button type="submit" className="m-4 px-5 btn btn-light">Cancel</button>
                            </div>

                        </div>


                    </form>
                </div>
            </div >

        )
    }
}

export default Doctorform;
