import React, { Component } from 'react';
import Nav from './Nav';
import axios from 'axios';
import Modal from 'react-bootstrap/Modal';



class Ward extends Component {

    constructor(props) {
        super(props);
        this.state = {
            wardlist: [],
            loading: false,
            modalIsOpen: false,
        }
    }


    async componentDidMount() {
        this.setState({ loading: true });
        const response = await axios.get('http://localhost:8080/hospitalApp/ward/all') // GET meth
        console.log(response.data);
        this.setState({
            loading: false,
            wardlist: response.data
        })
        console.log(this.state.wardlist)

    }


    handlemodeview = (item) => {
        const index = this.state.wardlist.indexOf(item);
        this.setState({
            modalIsOpen: true
        })


        console.log(item);
    }

    handleClose = () => {
        console.log("close")
        this.setState({

            modalIsOpen: false
        })


    }


    render() {
        const { wardlist } = this.state;
        return (
            <div>
                <Nav user2={this.props.user} />

                <div>
                    {this.state.loading &&
                        <div class="d-flex justify-content-center">
                            <div class="spinner-border" role="status">
                                <span class="sr-only">Loading...</span>
                            </div>
                        </div>}
                    <div>



                        {/* } */}


                        <div className="row g-3">
                            {wardlist.map((item, index) => {

                                return (

                                    <div className="mx-2">
                                        <div className="card m-4">
                                            <div class="card-body mx-2">
                                                <div className="row">
                                                    <h5 className="card-title mx-4">{item.wardType}</h5>

                                                    <div>
                                                        <button type="button" onClick={() => this.handlemodeview(item)}
                                                            className="btn btn-primary button1 pt-1  d-flex flex-row-reverse"

                                                        >view</button>
                                                    </div>
                                                    <div class="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                                        <div class="modal-dialog">
                                                            <div class="modal-content">
                                                                <div class="modal-header">
                                                                    <h5 class="modal-title" id="exampleModalLabel">{wardlist.WardId}</h5>



                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                <p class="card-text">Available Beds:<b className="text-danger">{item.availableBeds}</b></p>
                                                <p class="card-text">Total Beds :<b className="text-success">{item.totalBeds}</b></p>
                                            </div>
                                        </div>
                                    </div>

                                )
                            })
                            }
                        </div>
                        <Modal show={this.state.modalIsOpen} onHide={this.handleClose}>
                            <Modal.Header closeButton>
                                <Modal.Title className="w-100">


                                    <table className="table table-striped" >
                                        <thead>
                                            <tr >
                                                <td ><b>First</b></td>
                                                <td ><b>Last</b></td>
                                                <td ><b>Specialization</b></td>

                                                <td ><b>Available</b></td>

                                                <td ><b>Mobile</b></td>

                                            </tr>
                                        </thead>


                                        <tbody>
                                            <tr>
                                                <td>{wardlist.wardType}</td>
                                                {/* <td>{doctor.lName}</td>
                                        <td>{doctor.specialization}</td>
                                        <td>{doctor.isAvailable.toString()}</td>
                                        <td>{doctor.mobileNum}</td> */}
                                            </tr>
                                        </tbody>
                                    </table>

                                </Modal.Title>
                            </Modal.Header>
                            <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>

                        </Modal>

                    </div>


                </div>

            </div>


        )
    }

}

export default Ward;
