import React, { Component } from 'react';
import Nav from './Nav';

import axios from 'axios';


class Ward extends Component {
    constructor() {
        super();
        this.state = {
            wardlist: [],
            loading: false,
        }
    }

    async componentDidMount() {
        const response = await axios.get('http://localhost:8080/hospitalApp/ward/all') // GET meth
        console.log(response.data);
        this.setState({
            loading: false,
            wardlist: response.data
        })
        console.log(this.state.wardlist)
    }



    render() {

        const { wardlist, index } = this.state
        return (
            <div>
                <Nav />

                <div>

                    <div className="row g-3">
                        {wardlist.map((item, key) => {

                            return (

                                <div class="card m-4">

                                    <div class="card-body mx-2">
                                        <h5 class="card-title">{item.wardType}</h5>
                                        <p class="card-text">Available Beds:<b className="text-danger">{item.availableBeds}</b></p>
                                        <p class="card-text"><small class="text-muted">Total Beds :<b className="text-success">{item.totalBeds}</b></small></p>
                                    </div>
                                </div>
                            )
                        })
                        }
                    </div>

                </div>

            </div >
        )
    }

}

export default Ward;
