import React, { Component } from "react";


class Doctorform extends Component {
    constructor() {
        super();
        this.state = {
            firstname: '',
            lastname: '',
            formErrors: {}
        }
        this.initialState = this.state;
    }
    handleFormValidation() {
        const { firstname, lastname } = this.state;
        let formErrors = {};
        let formIsValid = true;

        if (!firstname) {
            formIsValid = false;
            formErrors["firstnameErr"] = "Name is required.";
        }
        console.log(firstname);


        if (!lastname) {
            formIsValid = false;
            formErrors["lastnameErr"] = "Name is required.";
        }
        console.log(lastname);
        this.setState({ formErrors: formErrors });
        return formIsValid;



    }
    handleChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value });
        console.log(value)

    }


    handleSubmit = (e) => {
        e.preventDefault();
        if (this.handleFormValidation()) {
            alert('You have been successfully registered.')
            this.setState(this.initialState)
        }
        console.log(this.initialState)
    }
    render() {
        const { firstnameErr, lastnameErr, emailIdErr, dobErr, genderErr, phoneNumberErr, cityErr } = this.state.formErrors;


        return (

            <div className="container ">

                <div className="border border-primary doc-form m-5 p-4">
                    <h3 className="m-2">Registration From</h3>
                    <form onSubmit={this.handleSubmit}>

                        <div class="form-row  ">
                            <div className=" form-group col-md-4">
                                <label >FirstName</label>
                                <input type="text"
                                    name="firstname"
                                    value={this.state.firstname}
                                    onChange={this.handleChange}
                                    className="form-control"


                                    className={firstnameErr ? ' firstnameErr' : ''}

                                />
                                {firstnameErr &&
                                    <div style={{ color: "red", paddingBottom: 10 }}>{firstnameErr}</div>
                                }
                            </div>
                            <div className="form-group col-md-4">
                                <label >LastName</label>
                                <input type="text"
                                    name="lastname"
                                    value={this.state.lastname}
                                    onChange={this.handleChange} required
                                    className="form-control" placeholder="LastName" />
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-4">
                                <label for="inputPassword4">Mobile</label>
                                <input type="password" className="form-control" id="inputPassword4" placeholder="Mobile" />
                            </div>
                            <div className="form-group col-md-4">
                                <label for="inputPassword4">Email</label>
                                <input type="password" className="form-control" id="inputPassword4" placeholder="Email" />
                            </div>
                        </div>

                        <div className="form-row">
                            <div className="form-group col-md-4">
                                <label for="inputState">Specialization</label>
                                <select id="inputState" className="form-control">
                                    <option selected>Choose...</option>
                                    <option>Dental</option>
                                    <option>Cardio</option>
                                    <option>Diagnostic</option>
                                    <option>ENT</option>
                                </select>
                            </div>
                            <div className="form-group col-md-4">
                                <label for="inputState">Available</label>
                                <select id="inputState" className="form-control">
                                    <option selected>Choose...</option>
                                    <option>True</option>
                                    <option>False</option>
                                </select>
                            </div>
                        </div>
                        <div className="form-row">
                            <div className="form-group col-md-4">
                                <label for="inputState">Ward</label>
                                <select id="inputState" className="form-control">
                                    <option selected>Choose...</option>
                                    <option>General </option>
                                    <option>ICU</option>
                                    <option>NID</option>
                                </select>
                            </div>

                            <div className="form-group my-2 col-md-6">

                                <button type="submit" className=" mx-0 px-5 btn btn-primary">Sign in</button>

                                <button type="submit" className="m-4 px-5 btn btn-light">Cancel</button>
                            </div>

                        </div>


                    </form>
                </div>
            </div >

        )
    }
}

export default Doctorform;
