import React, { Component } from 'react';
import Nav from './Nav';
import axios from 'axios';


class Ward extends Component {

    constructor(props) {
        super(props);
        this.state = {
            wardlist: [],
            loading: false,
            modalIsOpen: false,
        }
    }


    async componentDidMount() {
        this.setState({ loading: true });
        const response = await axios.get('http://localhost:8080/hospitalApp/ward/all') // GET meth
        console.log(response.data);
        this.setState({
            loading: false,
            wardlist: response.data
        })
        console.log(this.state.wardlist)

    }


    handlemodeview = (item) => {
        const index = this.state.wardlist.indexOf(item);
        this.setState({
            modalIsOpen: true
        })


        console.log(item);
    }


    render() {
        const { wardlist } = this.state
        return (
            <div>
                <Nav user2={this.props.user} />

                <div>
                    {this.state.loading &&
                        <div class="d-flex justify-content-center">
                            <div class="spinner-border" role="status">
                                <span class="sr-only">Loading...</span>
                            </div>
                        </div>}
                    <div>
                        {
                            this.state.modalIsOpen === true &&
                            < div class="modal" tabindex="-1">
                                <div class="modal-dialog">
                                    <div class="modal-content">
                                        <div class="modal-header">
                                            <h5 class="modal-title">Modal title</h5>
                                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                                        </div>
                                        <div class="modal-body">
                                            <p>Modal body text goes here.</p>
                                        </div>
                                        <div class="modal-footer">
                                            <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                                            <button type="button" class="btn btn-primary">Save changes</button>
                                        </div>
                                    </div>
                                </div>
                            </div>

                        }



                        <div className="row g-3">
                            {wardlist.map((item, index) => {

                                return (
                                    <div>
                                        <div className="card m-4">
                                            <div class="card-body mx-2">
                                                <div className="row">
                                                    <h5 key={index} className="card-title mx-4">{item.wardType}</h5>

                                                    <div>
                                                        <button type="button" onClick={() => this.handlemodeview(item)}
                                                            className="btn btn-primary button1 pt-1  d-flex flex-row-reverse"
                                                            data-bs-toggle="modal" data-bs-target="#exampleModal"
                                                        >view</button>
                                                        {
                                                            this.state.modalIsOpen === true &&
                                                            <div>                                               <p>{item.wardType}</p>
                                                                <div class="modal-dialog modal-xl">...</div>
                                                                <div class="modal-dialog modal-lg">...</div>
                                                                <div class="modal-dialog modal-sm">...</div>
                                                            </div>
                                                        }
                                                    </div>

                                                    {/* ____ */}
                                                    <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                                        <div class="modal-dialog">
                                                            <div class="modal-content">
                                                                <div class="modal-header">
                                                                    <h5 class="modal-title" id="exampleModalLabel">{wardlist.WardId}</h5>



                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                <p class="card-text">Available Beds:<b className="text-danger">{item.availableBeds}</b></p>
                                                <p class="card-text">Total Beds :<b className="text-success">{item.totalBeds}</b></p>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                            }
                        </div>


                    </div>

                </div>
            </div >

        )
    }

}

export default Ward;
