import React, { Component } from 'react';
import Nav from './Nav';
import axios from 'axios';
import Modal from "react-modal";


let modalIsOpen = false;
class Ward extends Component {


    constructor(props) {
        super(props);
        this.state = {
            wardlist: [],
            loading: false,
        }
    }


    async componentDidMount() {
        this.setState({ loading: true });
        const response = await axios.get('http://localhost:8080/hospitalApp/ward/all') // GET meth
        console.log(response.data);
        this.setState({
            loading: false,
            wardlist: response.data
        })
        console.log(this.state.wardlist)

    }


    handlemodeview = (item) => {
        const index = this.state.wardlist.indexOf(item);


        this.setState({
            modalIsOpen: true,
        })

    }




    render() {

        const { wardlist, modalIsOpen } = this.state
        return (
            <div>
                <Nav user2={this.props.user} />

                <div>
                    {this.state.loading &&
                        <div className="d-flex justify-content-center">
                            <div className="spinner-border" role="status">
                                <span className="sr-only">Loading...</span>
                            </div>
                        </div>}
                    <div>




                        <div className="row g-3">
                            {wardlist.map((item, index) => {

                                return (
                                    <div>
                                        <div className="card m-4">
                                            <div className="card-body mx-2">
                                                <div className="row">
                                                    <h5 key={index} className="card-title mx-4">{item.wardType}</h5>


                                                    <button type="button" onClick={() => this.handlemodeview(item)}
                                                        className="btn btn-primary button1 pt-1  d-flex flex-row-reverse"
                                                        data-bs-toggle="modal" data-bs-target="#exampleModal"
                                                    >view</button>
                                                    <div>
                                                        <Modal isOpen={modalIsOpen}  >
                                                            <div className="container">
                                                                <p key={index} className="text-primary">{item.wardType}</p>
                                                                <p>{item.doctorsList.fName}</p>

                                                            </div>

                                                        </Modal>
                                                    </div>
                                                    <div class="modal fade" id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                                                        <div class="modal-dialog">
                                                            <div class="modal-content">
                                                                <div class="modal-header">
                                                                    <h5 class="modal-title" id="exampleModalLabel">{wardlist.WardId}</h5>



                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                                <p class="card-text">Available Beds:<b className="text-danger">{item.availableBeds}</b></p>
                                                <p class="card-text">Total Beds :<b className="text-success">{item.totalBeds}</b></p>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                            }
                        </div>


                    </div>

                </div>
            </div >

        )
    }

}

export default Ward;
